use rust_rpg_game::AssetsLoader;
use rust_rpg_game::ConvexHull;
use wasm_bindgen_test::{wasm_bindgen_test, wasm_bindgen_test_configure};

wasm_bindgen_test_configure!(run_in_browser);

#[wasm_bindgen_test]
async fn test_convex_hull() {
    let assets_loader = AssetsLoader {
        base_path: "".to_string(),
        ..Default::default()
    };

    let image = assets_loader
        .load_image("data:image/png;base64,")
        .await
        .expect("image loaded");

    let mut algo = ConvexHull::new(100, 100).expect("convex hull created");
    let hull = algo.wrap_image(&image).expect("hull");

    assert!(hull.is_empty(), "{:?}", hull);
}
